  String vodPic;
  String vodName;
  String vodEpo;
  String watchedDuration;
  String totalDuration;
  VideoHistoryItem(
      {required this.vodId,
      required this.vodPic,
      required this.vodName,
      required this.vodEpo,
      required this.watchedDuration,
      required this.totalDuration});
}

